import 'package:hive/hive.dart';import '../api/products/product.dart';class ProductItemsModelAdapter extends TypeAdapter<ProductData> {  @override  ProductData read(BinaryReader reader) {    return ProductData.fromJson(reader.readMap().cast());  }  @override  int get typeId => 0;  @override  void write(BinaryWriter writer, ProductData obj) {    writer.writeMap(obj.toJson());  }}